import { Router } from 'express'
import { z } from 'zod'
import { storage } from './storage'
import { propertySchema, applicationSchema, userSchema } from '@shared/schema'

const router = Router()

// User routes
router.post('/api/users', async (req, res) => {
  try {
    const userData = userSchema.omit({ id: true, createdAt: true, updatedAt: true }).parse(req.body)
    const user = await storage.createUser(userData)
    res.json(user)
  } catch (error) {
    res.status(400).json({ error: 'Invalid user data' })
  }
})

router.get('/api/users/:id', async (req, res) => {
  try {
    const user = await storage.getUserById(req.params.id)
    if (!user) {
      return res.status(404).json({ error: 'User not found' })
    }
    res.json(user)
  } catch (error) {
    res.status(500).json({ error: 'Server error' })
  }
})

// Property routes
router.post('/api/properties', async (req, res) => {
  try {
    const propertyData = propertySchema.omit({ id: true, createdAt: true, updatedAt: true }).parse(req.body)
    const property = await storage.createProperty(propertyData)
    res.json(property)
  } catch (error) {
    res.status(400).json({ error: 'Invalid property data' })
  }
})

router.get('/api/properties', async (req, res) => {
  try {
    const filters = {
      landlordId: req.query.landlordId as string,
      featured: req.query.featured === 'true' ? true : req.query.featured === 'false' ? false : undefined
    }
    const properties = await storage.getProperties(filters)
    res.json(properties)
  } catch (error) {
    res.status(500).json({ error: 'Server error' })
  }
})

router.get('/api/properties/:id', async (req, res) => {
  try {
    const property = await storage.getPropertyById(req.params.id)
    if (!property) {
      return res.status(404).json({ error: 'Property not found' })
    }
    res.json(property)
  } catch (error) {
    res.status(500).json({ error: 'Server error' })
  }
})

router.put('/api/properties/:id', async (req, res) => {
  try {
    const updates = propertySchema.partial().parse(req.body)
    const property = await storage.updateProperty(req.params.id, updates)
    if (!property) {
      return res.status(404).json({ error: 'Property not found' })
    }
    res.json(property)
  } catch (error) {
    res.status(400).json({ error: 'Invalid property data' })
  }
})

router.delete('/api/properties/:id', async (req, res) => {
  try {
    const deleted = await storage.deleteProperty(req.params.id)
    if (!deleted) {
      return res.status(404).json({ error: 'Property not found' })
    }
    res.json({ success: true })
  } catch (error) {
    res.status(500).json({ error: 'Server error' })
  }
})

// Application routes
router.post('/api/applications', async (req, res) => {
  try {
    const applicationData = applicationSchema.omit({ id: true, createdAt: true, updatedAt: true }).parse(req.body)
    const application = await storage.createApplication(applicationData)
    res.json(application)
  } catch (error) {
    res.status(400).json({ error: 'Invalid application data' })
  }
})

router.get('/api/applications', async (req, res) => {
  try {
    const filters = {
      propertyId: req.query.propertyId as string,
      tenantId: req.query.tenantId as string,
      landlordId: req.query.landlordId as string
    }
    const applications = await storage.getApplications(filters)
    res.json(applications)
  } catch (error) {
    res.status(500).json({ error: 'Server error' })
  }
})

router.get('/api/applications/:id', async (req, res) => {
  try {
    const application = await storage.getApplicationById(req.params.id)
    if (!application) {
      return res.status(404).json({ error: 'Application not found' })
    }
    res.json(application)
  } catch (error) {
    res.status(500).json({ error: 'Server error' })
  }
})

router.put('/api/applications/:id', async (req, res) => {
  try {
    const updates = applicationSchema.partial().parse(req.body)
    const application = await storage.updateApplication(req.params.id, updates)
    if (!application) {
      return res.status(404).json({ error: 'Application not found' })
    }
    res.json(application)
  } catch (error) {
    res.status(400).json({ error: 'Invalid application data' })
  }
})

router.delete('/api/applications/:id', async (req, res) => {
  try {
    const deleted = await storage.deleteApplication(req.params.id)
    if (!deleted) {
      return res.status(404).json({ error: 'Application not found' })
    }
    res.json({ success: true })
  } catch (error) {
    res.status(500).json({ error: 'Server error' })
  }
})

export default router

server/storage.ts.content:
import { Property, Application, User } from '@shared/schema'

export interface IStorage {
  // User operations
  createUser(user: Omit<User, 'id' | 'createdAt' | 'updatedAt'>): Promise<User>
  getUserById(id: string): Promise<User | null>
  getUserByEmail(email: string): Promise<User | null>
  updateUser(id: string, updates: Partial<User>): Promise<User | null>

  // Property operations
  createProperty(property: Omit<Property, 'id' | 'createdAt' | 'updatedAt'>): Promise<Property>
  getPropertyById(id: string): Promise<Property | null>
  getProperties(filters?: { landlordId?: string; featured?: boolean }): Promise<Property[]>
  updateProperty(id: string, updates: Partial<Property>): Promise<Property | null>
  deleteProperty(id: string): Promise<boolean>

  // Application operations
  createApplication(application: Omit<Application, 'id' | 'createdAt' | 'updatedAt'>): Promise<Application>
  getApplicationById(id: string): Promise<Application | null>
  getApplications(filters?: { propertyId?: string; tenantId?: string; landlordId?: string }): Promise<Application[]>
  updateApplication(id: string, updates: Partial<Application>): Promise<Application | null>
  deleteApplication(id: string): Promise<boolean>
}

export class MemStorage implements IStorage {
  private users: Map<string, User> = new Map()
  private properties: Map<string, Property> = new Map()
  private applications: Map<string, Application> = new Map()

  private generateId(): string {
    return Date.now().toString(36) + Math.random().toString(36).substr(2)
  }

  // User operations
  async createUser(userData: Omit<User, 'id' | 'createdAt' | 'updatedAt'>): Promise<User> {
    const user: User = {
      ...userData,
      id: this.generateId(),
      createdAt: new Date(),
      updatedAt: new Date(),
    }
    this.users.set(user.id, user)
    return user
  }

  async getUserById(id: string): Promise<User | null> {
    return this.users.get(id) || null
  }

  async getUserByEmail(email: string): Promise<User | null> {
    for (const user of this.users.values()) {
      if (user.email === email) {
        return user
      }
    }
    return null
  }

  async updateUser(id: string, updates: Partial<User>): Promise<User | null> {
    const user = this.users.get(id)
    if (!user) return null

    const updatedUser = { ...user, ...updates, updatedAt: new Date() }
    this.users.set(id, updatedUser)
    return updatedUser
  }

  // Property operations
  async createProperty(propertyData: Omit<Property, 'id' | 'createdAt' | 'updatedAt'>): Promise<Property> {
    const property: Property = {
      ...propertyData,
      id: this.generateId(),
      createdAt: new Date(),
      updatedAt: new Date(),
    }
    this.properties.set(property.id, property)
    return property
  }

  async getPropertyById(id: string): Promise<Property | null> {
    return this.properties.get(id) || null
  }

  async getProperties(filters?: { landlordId?: string; featured?: boolean }): Promise<Property[]> {
    let properties = Array.from(this.properties.values())

    if (filters?.landlordId) {
      properties = properties.filter(p => p.landlordId === filters.landlordId)
    }

    if (filters?.featured !== undefined) {
      properties = properties.filter(p => p.featured === filters.featured)
    }

    return properties
  }

  async updateProperty(id: string, updates: Partial<Property>): Promise<Property | null> {
    const property = this.properties.get(id)
    if (!property) return null

    const updatedProperty = { ...property, ...updates, updatedAt: new Date() }
    this.properties.set(id, updatedProperty)
    return updatedProperty
  }

  async deleteProperty(id: string): Promise<boolean> {
    return this.properties.delete(id)
  }

  // Application operations
  async createApplication(applicationData: Omit<Application, 'id' | 'createdAt' | 'updatedAt'>): Promise<Application> {
    const application: Application = {
      ...applicationData,
      id: this.generateId(),
      createdAt: new Date(),
      updatedAt: new Date(),
    }
    this.applications.set(application.id, application)
    return application
  }

  async getApplicationById(id: string): Promise<Application | null> {
    return this.applications.get(id) || null
  }

  async getApplications(filters?: { propertyId?: string; tenantId?: string; landlordId?: string }): Promise<Application[]> {
    let applications = Array.from(this.applications.values())

    if (filters?.propertyId) {
      applications = applications.filter(a => a.propertyId === filters.propertyId)
    }

    if (filters?.tenantId) {
      applications = applications.filter(a => a.tenantId === filters.tenantId)
    }

    if (filters?.landlordId) {
      applications = applications.filter(a => a.landlordId === filters.landlordId)
    }

    return applications
  }

  async updateApplication(id: string, updates: Partial<Application>): Promise<Application | null> {
    const application = this.applications.get(id)
    if (!application) return null

    const updatedApplication = { ...application, ...updates, updatedAt: new Date() }
    this.applications.set(id, updatedApplication)
    return updatedApplication
  }

  async deleteApplication(id: string): Promise<boolean> {
    return this.applications.delete(id)
  }
}

export const storage = new MemStorage()

