'use client'

import { createContext, useContext, useState, useEffect, ReactNode } from 'react'

interface Country {
  code: string
  name: string
  flag: string
  currency: string
  currencySymbol: string
}

interface CountryContextType {
  selectedCountry: Country
  setSelectedCountry: (country: Country) => void
  formatPrice: (price: number) => string
}

const countries: Country[] = [
  {
    code: 'NG',
    name: 'Nigeria',
    flag: '🇳🇬',
    currency: 'NGN',
    currencySymbol: '₦'
  },
  {
    code: 'GH',
    name: 'Ghana',
    flag: '🇬🇭',
    currency: 'GHS',
    currencySymbol: '₵'
  },
  {
    code: 'ZA',
    name: 'South Africa',
    flag: '🇿🇦',
    currency: 'ZAR',
    currencySymbol: 'R'
  },
  {
    code: 'US',
    name: 'United States',
    flag: '🇺🇸',
    currency: 'USD',
    currencySymbol: '$'
  }
]

const CountryContext = createContext<CountryContextType | undefined>(undefined)

export function CountryProvider({ children }: { children: ReactNode }) {
  const [selectedCountry, setSelectedCountry] = useState<Country>(countries[0]) // Default to Nigeria

  const formatPrice = (price: number) => {
    const currencyConfig = {
      NG: { style: 'currency', currency: 'NGN', minimumFractionDigits: 0 },
      GH: { style: 'currency', currency: 'GHS', minimumFractionDigits: 2 },
      ZA: { style: 'currency', currency: 'ZAR', minimumFractionDigits: 2 },
      US: { style: 'currency', currency: 'USD', minimumFractionDigits: 2 }
    }

    const config = currencyConfig[selectedCountry.code as keyof typeof currencyConfig]

    try {
      return new Intl.NumberFormat('en-US', config).format(price)
    } catch (error) {
      return `${selectedCountry.currencySymbol}${price.toLocaleString()}`
    }
  }

  return (
    <CountryContext.Provider value={{
      selectedCountry,
      setSelectedCountry,
      formatPrice
    }}>
      {children}
    </CountryContext.Provider>
  )
}

export function useCountry() {
  const context = useContext(CountryContext)
  if (context === undefined) {
    throw new Error('useCountry must be used within a CountryProvider')
  }
  return context
}

src/contexts/favorites-context.tsx.content:
'use client'

import React, { createContext, useContext, useState, useEffect } from 'react'
import { useAuth } from './auth-context'
import { db } from '@/lib/firebase'
import { doc, getDoc, setDoc, updateDoc, arrayUnion, arrayRemove } from 'firebase/firestore'
import { toast } from 'react-hot-toast'

interface FavoritesContextType {
  favorites: string[]
  addToFavorites: (propertyId: string) => Promise<void>
  removeFromFavorites: (propertyId: string) => Promise<void>
  isFavorite: (propertyId: string) => boolean
  loading: boolean
}

const FavoritesContext = createContext<FavoritesContextType | undefined>(undefined)

export function useFavorites() {
  const context = useContext(FavoritesContext)
  if (context === undefined) {
    throw new Error('useFavorites must be used within a FavoritesProvider')
  }
  return context
}

export function FavoritesProvider({ children }: { children: React.ReactNode }) {
  const { user } = useAuth()
  const [favorites, setFavorites] = useState<string[]>([])
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const userId = user?.id || user?.uid
    console.log('Favorites context - user state:', user ? 'logged in' : 'not logged in', 'userId:', userId)

    if (!userId) {
      setFavorites([])
      setLoading(false)
      return
    }

    const loadFavorites = async () => {
      try {
        console.log('Loading favorites for user:', userId)
        const favoritesRef = doc(db, 'users', userId, 'favorites', 'userFavorites')
        const favoritesSnap = await getDoc(favoritesRef)

        if (favoritesSnap.exists()) {
          const data = favoritesSnap.data()
          console.log('Favorites data loaded:', data)
          setFavorites(data.properties || [])
        } else {
          // Create initial favorites document if it doesn't exist
          console.log('Creating initial favorites document for user:', userId)
          await setDoc(favoritesRef, {
            userId: userId,
            properties: [],
            createdAt: new Date(),
            updatedAt: new Date()
          })
          setFavorites([])
        }
      } catch (error) {
        console.error('Error loading favorites:', error)
        setFavorites([])
      } finally {
        setLoading(false)
      }
    }

    loadFavorites()
  }, [user])

  const addToFavorites = async (propertyId: string) => {
    const userId = user?.id || user?.uid
    console.log('Adding to favorites - user:', userId, 'property:', propertyId)

    if (!userId) {
      console.log('No user found for favorites')
      toast.error('Please login to save favorites')
      return
    }

    try {
      const favoritesRef = doc(db, 'users', userId, 'favorites', 'userFavorites')
      const favoritesSnap = await getDoc(favoritesRef)

      if (favoritesSnap.exists()) {
        await updateDoc(favoritesRef, {
          properties: arrayUnion(propertyId),
          updatedAt: new Date()
        })
      } else {
        await setDoc(favoritesRef, {
          userId: userId,
          properties: [propertyId],
          createdAt: new Date(),
          updatedAt: new Date()
        })
      }

      setFavorites(prev => [...prev, propertyId])
      toast.success('Added to favorites')
      console.log('Successfully added to favorites')
    } catch (error) {
      console.error('Error adding to favorites:', error)
      toast.error('Failed to add to favorites')
    }
  }

  const removeFromFavorites = async (propertyId: string) => {
    const userId = user?.id || user?.uid
    if (!userId) return

    try {
      const favoritesRef = doc(db, 'users', userId, 'favorites', 'userFavorites')
      await updateDoc(favoritesRef, {
        properties: arrayRemove(propertyId),
        updatedAt: new Date()
      })

      setFavorites(prev => prev.filter(id => id !== propertyId))
      toast.success('Removed from favorites')
    } catch (error) {
      console.error('Error removing from favorites:', error)
      toast.error('Failed to remove from favorites')
    }
  }

  const isFavorite = (propertyId: string) => {
    return favorites.includes(propertyId)
  }

  return (
    <FavoritesContext.Provider
      value={{
        favorites,
        addToFavorites,
        removeFromFavorites,
        isFavorite,
        loading
      }}
    >
      {children}
    </FavoritesContext.Provider>
  )
}

src/contexts/theme-context.tsx.content:
'use client'

import { createContext, useContext, useEffect, useState } from 'react'

type Theme = 'dark' | 'light' | 'system'

interface ThemeProviderProps {
  children: React.ReactNode
  defaultTheme?: Theme
  storageKey?: string
  attribute?: string
  enableSystem?: boolean
  disableTransitionOnChange?: boolean
}

interface ThemeProviderState {
  theme: Theme
  setTheme: (theme: Theme) => void
}

const initialState: ThemeProviderState = {
  theme: 'system',
  setTheme: () => null,
}

const ThemeProviderContext = createContext<ThemeProviderState>(initialState)

export function ThemeProvider({
  children,
  defaultTheme = 'system',
  storageKey = 'rentnova-theme',
  attribute = 'class',
  enableSystem = true,
  disableTransitionOnChange = false,
  ...props
}: ThemeProviderProps) {
  const [theme, setTheme] = useState<Theme>(
    () => (typeof window !== 'undefined' && localStorage.getItem(storageKey) as Theme) || defaultTheme
  )

  useEffect(() => {
    const root = window.document.documentElement

    root.classList.remove('light', 'dark')

    if (theme === 'system' && enableSystem) {
      const systemTheme = window.matchMedia('(prefers-color-scheme: dark)').matches
        ? 'dark'
        : 'light'

      root.classList.add(systemTheme)
      return
    }

    root.classList.add(theme)
  }, [theme, enableSystem])

  const value = {
    theme,
    setTheme: (theme: Theme) => {
      localStorage.setItem(storageKey, theme)
      setTheme(theme)
    },
  }

  return (
    <ThemeProviderContext.Provider {...props} value={value}>
      {children}
    </ThemeProviderContext.Provider>
  )
}

export const useTheme = () => {
  const context = useContext(ThemeProviderContext)

  if (context === undefined)
    throw new Error('useTheme must be used within a ThemeProvider')

  return context
}

src/hooks/use-onboarding.ts.content:
'use client'

import { useState, useEffect } from 'react'
import { useAuth } from '@/contexts/auth-context'
import { doc, getDoc, setDoc } from 'firebase/firestore'
import { db } from '@/lib/firebase'

const ONBOARDING_STORAGE_KEY = 'rentnova_onboarding_completed'

export function useOnboarding() {
  const { user } = useAuth()
  const [hasCompletedOnboarding, setHasCompletedOnboarding] = useState(true)
  const [isOnboardingOpen, setIsOnboardingOpen] = useState(false)

  useEffect(() => {
    const checkOnboardingStatus = async () => {
      if (user) {
        try {
          // First check Firebase for persistent status
          const userDocRef = doc(db, 'users', user.uid)
          const userDoc = await getDoc(userDocRef)

          let hasCompleted = false
          if (userDoc.exists()) {
            hasCompleted = userDoc.data().hasSeenOnboarding || false
          }

          // Fallback to localStorage if Firebase is unavailable
          if (!hasCompleted) {
            const localCompleted = localStorage.getItem(`${ONBOARDING_STORAGE_KEY}_${user.id}`)
            hasCompleted = localCompleted === 'true'
          }

          setHasCompletedOnboarding(hasCompleted)

          // Show onboarding for new users after a short delay
          if (!hasCompleted) {
            const timer = setTimeout(() => {
              setIsOnboardingOpen(true)
            }, 1000)
            return () => clearTimeout(timer)
          }
        } catch (error) {
          console.error('Error checking onboarding status:', error)
          // Fallback to localStorage only
          const localCompleted = localStorage.getItem(`${ONBOARDING_STORAGE_KEY}_${user.id}`)
          const hasCompleted = localCompleted === 'true'
          setHasCompletedOnboarding(hasCompleted)

          if (!hasCompleted) {
            const timer = setTimeout(() => {
              setIsOnboardingOpen(true)
            }, 1000)
            return () => clearTimeout(timer)
          }
        }
      }
    }

    checkOnboardingStatus()
  }, [user])

  const startOnboarding = () => {
    setIsOnboardingOpen(true)
  }

  const completeOnboarding = async () => {
    if (user) {
      try {
        // Save to Firebase for persistent tracking across devices
        const userDocRef = doc(db, 'users', user.uid)
        await setDoc(userDocRef, {
          hasSeenOnboarding: true,
          onboardingCompletedAt: new Date()
        }, { merge: true })

        // Also save locally for immediate access
        localStorage.setItem(`${ONBOARDING_STORAGE_KEY}_${user.id}`, 'true')
        setHasCompletedOnboarding(true)
        setIsOnboardingOpen(false)
      } catch (error) {
        console.error('Error saving onboarding status:', error)
        // Fallback to localStorage only
        localStorage.setItem(`${ONBOARDING_STORAGE_KEY}_${user.id}`, 'true')
        setHasCompletedOnboarding(true)
        setIsOnboardingOpen(false)
      }
    }
  }

  const resetOnboarding = () => {
    if (user) {
      localStorage.removeItem(`${ONBOARDING_STORAGE_KEY}_${user.id}`)
      setHasCompletedOnboarding(false)
    }
  }

  const closeOnboarding = () => {
    setIsOnboardingOpen(false)
  }

  return {
    hasCompletedOnboarding,
    isOnboardingOpen,
    startOnboarding,
    completeOnboarding,
    resetOnboarding,
    closeOnboarding
  }
}

