    utilities: [] as string[],

    // Media  
    imageUrls: [] as string[],
    images: [] as string[], // Alternative field name for compatibility
    videoUrl: ''
  })

  // Protect route - redirect if not authenticated
  useEffect(() => {
    if (!loading && !user) {
      router.push('/auth/login')
    }
  }, [user, loading, router])

  // Pre-fill data when editing
  useEffect(() => {
    if (existingProperty && isEditing) {
      setPropertyData({
        propertyTitle: existingProperty.propertyTitle || '',
        address: existingProperty.address || '',
        city: existingProperty.city || '',
        state: existingProperty.state || '',
        country: existingProperty.country || 'Nigeria',
        propertyType: existingProperty.propertyType || '',
        propertyPurpose: existingProperty.propertyPurpose || 'rent',
        bedroomCount: existingProperty.bedroomCount || 0,
        toiletCount: existingProperty.toiletCount || 0,
        kitchenCount: existingProperty.kitchenCount || 0,
        parkingCount: existingProperty.parkingCount || 0,
        size: existingProperty.size || 0,
        price: String(existingProperty.price || 0),
        rentPeriod: existingProperty.rentPeriod || 'monthly',
        requiresUpfrontDeposit: existingProperty.requiresUpfrontDeposit || false,
        depositYears: existingProperty.depositYears || 1,
        description: existingProperty.description || '',
        features: existingProperty.features || [],
        utilities: existingProperty.utilities || [],
        furnishings: existingProperty.furnishings || [],
        kitchenFeatures: existingProperty.kitchenFeatures || [],
        bathroomFeatures: existingProperty.bathroomFeatures || [],
        floorType: existingProperty.floorType || '',
        ceilingType: existingProperty.ceilingType || '',
        buildingAmenities: existingProperty.buildingAmenities || [],
        readyToLive: existingProperty.readyToLive ?? true,
        isFeatured: existingProperty.isFeatured || false,
        isAvailable: existingProperty.isAvailable ?? true,
        // Ensure imageUrls is always an array
        imageUrls: existingProperty.imageUrls || [],
        images: existingProperty.imageUrls || [] // Compatibility field
      })

      // Set image previews if available
      if (existingProperty.imageUrls && existingProperty.imageUrls.length > 0) {
        setImagesPreviews(existingProperty.imageUrls)
      }
    }
  }, [existingProperty, isEditing])

  // Show loading while checking authentication
  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full mx-auto mb-4" />
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    )
  }

  // Don't render if not authenticated
  if (!user) {
    return null
  }

  // Nigerian states for dropdown
  const nigerianStates = [
    'Abia', 'Adamawa', 'Akwa Ibom', 'Anambra', 'Bauchi', 'Bayelsa', 'Benue', 'Borno', 'Cross River',
    'Delta', 'Ebonyi', 'Edo', 'Ekiti', 'Enugu', 'FCT', 'Gombe', 'Imo', 'Jigawa', 'Kaduna', 'Kano',
    'Katsina', 'Kebbi', 'Kogi', 'Kwara', 'Lagos', 'Nasarawa', 'Niger', 'Ogun', 'Ondo', 'Osun',
    'Oyo', 'Plateau', 'Rivers', 'Sokoto', 'Taraba', 'Yobe', 'Zamfara'
  ]

  // Property types
  const propertyTypes = [
    'Apartment', 'House', 'Duplex', 'Bungalow', 'Flat', 'Studio', 'Room', 'Self Contain', 'Penthouse', 'Villa'
  ]

  // Feature options based on your schema
  const bathroomFeatureOptions = [
    'Ensuite', 'Water Heater', 'Shared', 'Bathtub', 'Shower', 'Modern Tiles', 'Hot Water'
  ]

  const buildingAmenityOptions = [
    'Parking Space', 'CCTV', 'Security Door', 'Veranda', 'Balcony', 'Swimming Pool', 'Gym', 
    'Elevator', 'Generator', 'Security Guard', 'Gated Community', 'Garden', 'Playground'
  ]

  const furnishingOptions = [
    'Bedframe', 'Fan', 'Mattress', 'Wardrobe', 'AC', 'Dining Table', 'Sofa', 'TV Stand', 
    'Refrigerator', 'Washing Machine', 'Microwave', 'Gas Cooker', 'Kitchen Cabinets'
  ]

  const kitchenFeatureOptions = [
    'Cabinet', 'Private', 'Sink', 'Gas Cooker', 'Electric Cooker', 'Microwave', 'Refrigerator Space',
    'Modern Tiles', 'Exhaust Fan', 'Kitchen Island', 'Pantry'
  ]

  const utilityOptions = [
    'Waste Disposal', 'Water Bills Included', 'Prepaid Meter', 'Shared Meter', 'Wi-Fi',
    'DSTV/Cable', 'Backup Generator', 'Solar Power', 'Borehole Water', 'Street Light'
  ]

  const ceilingTypes = ['POP', 'PVC', 'Wood', 'Concrete', 'Gypsum', 'Asbestos']
  const floorTypes = ['Tiles', 'Marble', 'Terrazzo', 'Wooden', 'Concrete', 'Carpet', 'Vinyl']

  const rentPeriods = [
    { value: 'weekly', label: 'Weekly', description: 'Rent paid every week' },
    { value: 'monthly', label: 'Monthly', description: 'Rent paid every month' },
    { value: 'yearly', label: 'Yearly', description: 'Rent paid annually' }
  ]

  const depositOptions = [
    { value: 1, label: '1 Year Advance', description: 'Tenant pays 1 year rent upfront' },
    { value: 2, label: '2 Years Advance', description: 'Tenant pays 2 years rent upfront' },
    { value: 3, label: '3 Years Advance', description: 'Tenant pays 3 years rent upfront' },
    { value: 5, label: '5 Years Advance', description: 'Tenant pays 5 years rent upfront' }
  ]

  // Currency formatting function
  const formatCurrency = (value: string | number) => {
    // Convert to string first
    const stringValue = String(value || '')

    // Remove non-numeric characters except decimal point
    const numericValue = stringValue.replace(/[^\d.]/g, '')

    // Handle empty or invalid input
    if (!numericValue || numericValue === '.') return ''

    // Parse as number and format with thousands separator
    const number = parseFloat(numericValue)
    if (isNaN(number)) return ''

    return number.toLocaleString('en-NG', {
      minimumFractionDigits: 0,
      maximumFractionDigits: 2
    })
  }

  // Handle price input with formatting
  const handlePriceChange = (value: string) => {
    const rawValue = value.replace(/[^\d.]/g, '')
    setPropertyData(prev => ({ ...prev, price: rawValue }))
  }

  // Validate price based on rent period
  const validatePrice = () => {
    const price = parseFloat(propertyData.price)
    if (!price || price <= 0) return { valid: false, message: 'Please enter a valid price' }

    return { valid: true, message: '' }
  }

  const handleInputChange = (field: string, value: string | number | boolean) => {
    setPropertyData(prev => ({ ...prev, [field]: value }))
  }

  const handleCheckboxChange = (category: string, option: string) => {
    setPropertyData(prev => {
      const currentArray = prev[category as keyof typeof prev] as string[]
      const isSelected = currentArray.includes(option)

      return {
        ...prev,
        [category]: isSelected 
          ? currentArray.filter(item => item !== option)
          : [...currentArray, option]
      }
    })
  }

  const handleSubmit = async () => {

    // Check if user is properly authenticated
    if (!firebaseUser?.uid || !user?.id) {
      toast.error('Authentication error. Please log in again.')
      router.push('/auth/login')
      return
    }

    // Enhanced validation with specific messages
    const validationErrors: string[] = []

    if (!propertyData.propertyType) validationErrors.push('Property type is required')
    if (!propertyData.address.trim()) validationErrors.push('Property address is required')
    if (!propertyData.city.trim()) validationErrors.push('City is required')
    if (!propertyData.state) validationErrors.push('State is required')
    if (!propertyData.description.trim()) validationErrors.push('Property description is required')

    const priceValidation = validatePrice()
    if (!priceValidation.valid) validationErrors.push(priceValidation.message)

    if (!propertyData.imageUrls || propertyData.imageUrls.length === 0) {
      validationErrors.push('At least one property photo is required')
    }

    if (propertyData.bedroomCount < 0) {
      validationErrors.push('Number of bedrooms cannot be negative')
    }

    if (propertyData.toiletCount < 1) {
      validationErrors.push('Property must have at least 1 toilet/bathroom')
    }

    if (propertyData.requiresUpfrontDeposit && !propertyData.depositYears) {
      validationErrors.push('Please specify deposit requirements')
    }

    // Show first validation error
    if (validationErrors.length > 0) {
      toast.error(validationErrors[0])
      return
    }

    setIsSubmitting(true)
    try {
      // Convert price to number for proper storage
      const numericPrice = parseFloat(propertyData.price)
      const numericSize = propertyData.size ? parseFloat(propertyData.size) : 0

      // Submit to Firebase with proper data types
      const propertyPayload = {
        // Basic property information
        address: propertyData.address.trim(),
        city: propertyData.city.trim(),
        state: propertyData.state,
        country: propertyData.country,
        description: propertyData.description.trim(),

        // Pricing information
        price: numericPrice,
        rentPeriod: propertyData.rentPeriod,
        size: numericSize,

        // Property details
        propertyType: propertyData.propertyType,
        propertyPurpose: propertyData.propertyPurpose,

        // Deposit requirements
        requiresUpfrontDeposit: propertyData.requiresUpfrontDeposit,
        depositYears: propertyData.depositYears,

        // Room configuration
        bedroomCount: propertyData.bedroomCount || 0,
        masterBedroomCount: propertyData.masterBedroomCount || 0,
        kitchenCount: propertyData.kitchenCount || 0,
        toiletCount: propertyData.toiletCount || 0,

        // Property specifications
        ceilingType: propertyData.ceilingType || '',
        floorType: propertyData.floorType || '',
        readyToLive: propertyData.readyToLive ?? true,
        readyToLiveDate: propertyData.readyToLiveDate || '',

        // Features arrays
        bathroomFeatures: propertyData.bathroomFeatures || [],
        buildingAmenities: propertyData.buildingAmenities || [],
        furnishings: propertyData.furnishings || [],
        kitchenFeatures: propertyData.kitchenFeatures || [],
        utilities: propertyData.utilities || [],

        // Media
        imageUrls: propertyData.imageUrls || [],
        videoUrl: propertyData.videoUrl || '',

        // Property title
        propertyTitle: propertyData.propertyTitle || `${propertyData.propertyType} in ${propertyData.city}`,

        // Owner and metadata
        ownerId: firebaseUser.uid,
        ownerEmail: firebaseUser.email || '',
        ownerName: firebaseUser.displayName || user?.firstName || 'Unknown User',
        status: isEditing ? (existingProperty?.status || 'pending') : 'pending', // New properties start as pending
        viewCount: isEditing ? (existingProperty?.viewCount || 0) : 0,
        isAvailable: propertyData.isAvailable ?? true,
        isFeatured: propertyData.isFeatured ?? false,
        updatedAt: serverTimestamp()
      }

      // Add createdAt only for new properties
      if (!isEditing) {
        propertyPayload.createdAt = serverTimestamp()
      }

      console.log('Property data to submit:', propertyPayload)

      if (isEditing && editId) {
        // Update existing property
        const propertyRef = doc(db, 'properties', editId)
        await updateDoc(propertyRef, propertyPayload)
        console.log('Property updated with ID:', editId)
        toast.success('Property updated successfully!')
      } else {
        // Add new property
        const docRef = await addDoc(collection(db, 'properties'), propertyPayload)
        console.log('Property created with ID:', docRef.id)
        toast.success('Property submitted successfully! It will be reviewed by our admin team.')
      }

      // Reset form and redirect after short delay
      setTimeout(() => {
        router.push('/dashboard/landlord/properties')
      }, 1500)

    } catch (error: any) {
      console.error('Error submitting property:', error)

      // Handle specific Firebase errors
      if (error.code === 'invalid-argument') {
        toast.error('Invalid property data. Please check all fields and try again.')
      } else if (error.code === 'permission-denied') {
        toast.error('You do not have permission to create properties.')
      } else if (error.code === 'failed-precondition') {
        toast.error('Database setup required. Please contact support.')
      } else {
        toast.error('Failed to submit property. Please try again.')
      }
    } finally {
      setIsSubmitting(false)
    }
  }

  // Address autocomplete using OpenStreetMap Nominatim
  const searchAddress = async (query: string) => {
    if (query.length < 3) {
      setAddressSuggestions([])
      setShowAddressSuggestions(false)
      return
    }

    try {
      const response = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(query)}&countrycodes=ng&limit=5&addressdetails=1`)
      const data = await response.json()

      const suggestions = data.map((item: any) => ({
        display_name: item.display_name,
        address: item.address || {},
        lat: item.lat,
        lon: item.lon
      }))

      setAddressSuggestions(suggestions)
      setShowAddressSuggestions(suggestions.length > 0)
    } catch (error) {
      console.error('Error searching address:', error)
      setAddressSuggestions([])
      setShowAddressSuggestions(false)
    }
  }

  const handleAddressSelect = (suggestion: any) => {
    const address = suggestion.address
    setPropertyData(prev => ({
      ...prev,
      address: suggestion.display_name.split(',')[0] || suggestion.display_name,
      city: address.city || address.town || address.village || '',
      state: address.state || '',
      country: address.country || 'Nigeria'
    }))
    setShowAddressSuggestions(false)
  }

  const handleAddressInputChange = (value: string) => {
    setPropertyData(prev => ({ ...prev, address: value }))
    searchAddress(value)
  }

  // Photo upload functionality
  const handlePhotoUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(event.target.files || [])

    if (files.length === 0) return

    // Check total images limit (10 max)
    if (uploadedImages.length + files.length > 10) {
      toast.error('Maximum 10 photos allowed')
      return
    }

    // Validate each file
    const validFiles: File[] = []
    for (const file of files) {
      if (file.size > 5 * 1024 * 1024) {
        toast.error(`${file.name} is too large (max 5MB)`)
        continue
      }

      if (!file.type.startsWith('image/')) {
        toast.error(`${file.name} is not a valid image`)
        continue
      }

      validFiles.push(file)
    }

    if (validFiles.length === 0) return

    setIsUploadingImages(true)

    // Create preview URLs
    const newPreviews = validFiles.map(file => URL.createObjectURL(file))

    setUploadedImages(prev => [...prev, ...validFiles])
    setImagesPreviews(prev => [...prev, ...newPreviews])

    // Convert to base64 for storage
    validFiles.forEach((file, index) => {
      const reader = new FileReader()
      reader.onload = (e) => {
        const base64 = e.target?.result as string
        setPropertyData(prev => ({
          ...prev,
          imageUrls: [...prev.imageUrls, base64]
        }))

        // If this is the last file, stop loading
        if (index === validFiles.length - 1) {
          setIsUploadingImages(false)
          toast.success(`${validFiles.length} photo(s) uploaded successfully`)
        }
      }
      reader.readAsDataURL(file)
    })

    // Clear the input
    if (event.target) event.target.value = ''
  }

  const removePhoto = (index: number) => {
    setUploadedImages(prev => prev.filter((_, i) => i !== index))
    setImagesPreviews(prev => {
      // Revoke the URL to free memory
      URL.revokeObjectURL(prev[index])
      return prev.filter((_, i) => i !== index)
    })
    setPropertyData(prev => ({
      ...prev,
      imageUrls: prev.imageUrls.filter((_, i) => i !== index)
    }))
    toast.success('Photo removed')
  }

  // Validate current step before proceeding
  const validateCurrentStep = () => {
    const errors: string[] = []

    switch (currentStep) {
      case 1: // Basic Information
        if (!propertyData.propertyType) errors.push('Property type is required')
        if (!propertyData.address.trim()) errors.push('Property address is required')
        if (!propertyData.city.trim()) errors.push('City is required')
        if (!propertyData.state) errors.push('State is required')
        if (!propertyData.description.trim()) errors.push('Property description is required')
        const priceValidation = validatePrice()
        if (!priceValidation.valid) errors.push(priceValidation.message)
        if (propertyData.bedroomCount < 0) errors.push('Number of bedrooms cannot be negative')
        if (propertyData.toiletCount < 1) errors.push('Property must have at least 1 toilet/bathroom')
        break

      case 2: // Features (all optional)
        break

      case 3: // Media
        if (!propertyData.imageUrls || propertyData.imageUrls.length === 0) {
          errors.push('At least one property photo is required')
        }
        break

      case 4: // Review (validation handled in submit)
        break
    }

    return errors
  }

  const handleNextStep = () => {
    const validationErrors = validateCurrentStep()

    if (validationErrors.length > 0) {
      toast.error(validationErrors[0])
      return
    }

    setCurrentStep(Math.min(steps.length, currentStep + 1))
  }

  const steps = [
    { id: 1, name: 'Basic Info', description: 'Property details and location' },
    { id: 2, name: 'Features', description: 'Amenities and utilities (optional)' },
    { id: 3, name: 'Media', description: 'Photos and videos' },
    { id: 4, name: 'Review', description: 'Review and submit' }
  ]

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div>
              <Button variant="outline" onClick={() => router.back()} className="mb-4">
                <ChevronLeft className="h-4 w-4 mr-2" />
                Back to Dashboard
              </Button>
              <h1 className="text-2xl font-bold text-gray-900">{isEditing ? 'Edit Property' : 'Add New Property'}</h1>
              <p className="text-gray-600">{isEditing ? 'Update your property listing details' : 'Create a new property listing for rent'}</p>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Progress Steps */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            {steps.map((step, index) => (
              <div key={step.id} className="flex items-center">
                <div className={`flex items-center justify-center w-8 h-8 rounded-full border-2 ${
                  currentStep >= step.id 
                    ? 'border-lavender-600 bg-lavender-600 text-white' 
                    : 'border-gray-300 text-gray-300'
                }`}>
                  {currentStep > step.id ? (
                    <Check className="h-5 w-5" />
                  ) : (
                    <span className="text-sm font-medium">{step.id}</span>
                  )}
                </div>
                {index < steps.length - 1 && (
                  <div className={`w-full h-0.5 mx-4 ${
                    currentStep > step.id ? 'bg-lavender-600' : 'bg-gray-300'
                  }`} />
                )}
              </div>
            ))}
          </div>
          <div className="mt-2">
            <p className="text-sm font-medium text-gray-900">{steps[currentStep - 1].name}</p>
            <p className="text-sm text-gray-600">{steps[currentStep - 1].description}</p>
          </div>
        </div>

        {/* Step Content */}
        <div className="space-y-6">
          {currentStep === 1 && (
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Building className="h-5 w-5 mr-2" />
                  Basic Property Information
                </CardTitle>
                <CardDescription>
                  Provide the essential details about your property
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-6">
                {/* Property Title */}
                <div>
                  <Label htmlFor="propertyTitle">Property Title</Label>
                  <Input
                    id="propertyTitle"
                    placeholder="e.g., Modern 3BR Apartment in Victoria Island"
                    value={propertyData.propertyTitle || ''}
                    onChange={(e) => handleInputChange('propertyTitle', e.target.value)}
                    className="mt-1"
                  />
                  <p className="text-sm text-gray-500 mt-1">
                    Give your property a catchy title (optional - we'll generate one if left empty)
                  </p>
                </div>

                {/* Property Type and Purpose */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="propertyType">Property Type *</Label>
                    <select
                      id="propertyType"
                      className="w-full mt-1 p-2 border border-gray-300 rounded-md"
                      value={propertyData.propertyType || ''}
                      onChange={(e) => handleInputChange('propertyType', e.target.value)}
                      required
                    >
                      <option value="">Select property type</option>
                      {propertyTypes.map(type => (
                        <option key={type} value={type}>{type}</option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <Label htmlFor="propertyPurpose">Purpose</Label>
                    <select
                      id="propertyPurpose"
                      className="w-full mt-1 p-2 border border-gray-300 rounded-md"
                      value={propertyData.propertyPurpose || 'rent'}
                      onChange={(e) => handleInputChange('propertyPurpose', e.target.value)}
                    >
                      <option value="rent">For Rent</option>
                      <option value="sale">For Sale</option>
                    </select>
                  </div>
                </div>

                {/* Location */}
                <div className="space-y-4">
                  <h4 className="font-medium text-gray-900">Location Details</h4>
                  <div className="relative">
                    <Label htmlFor="address">Full Address *</Label>
                    <div className="relative">
                      <Input
                        id="address"
                        placeholder="Start typing an address in Nigeria..."
                        value={propertyData.address || ''}
                        onChange={(e) => handleAddressInputChange(e.target.value)}
                        onFocus={() => propertyData.address.length >= 3 && setShowAddressSuggestions(true)}
                        onBlur={() => setTimeout(() => setShowAddressSuggestions(false), 200)}
                        required
                        className="pr-10"
                      />
                      <Search className="absolute right-3 top-3 h-4 w-4 text-gray-400" />
                    </div>

                    {/* Address Suggestions */}
                    {showAddressSuggestions && addressSuggestions.length > 0 && (
                      <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-md shadow-lg max-h-60 overflow-auto">
                        {addressSuggestions.map((suggestion, index) => (
                          <button
                            key={index}
                            type="button"
                            className="w-full px-4 py-2 text-left hover:bg-gray-50 border-b border-gray-100 last:border-b-0 text-sm"
                            onClick={() => handleAddressSelect(suggestion)}
                          >
                            <div className="font-medium text-gray-900">
                              {suggestion.display_name.split(',')[0]}
                            </div>
                            <div className="text-gray-600 text-xs">
                              {suggestion.display_name}
                            </div>
                          </button>
                        ))}
                      </div>
                    )}
                  </div>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div>
                      <Label htmlFor="city">City *</Label>
                      <Input
                        id="city"
                        placeholder="e.g., Lagos"
                        value={propertyData.city || ''}
                        onChange={(e) => handleInputChange('city', e.target.value)}
                        required
                      />
                    </div>
                    <div>
                      <Label htmlFor="state">State *</Label>
                      <select
                        id="state"
                        className="w-full p-2 border border-gray-300 rounded-md"
                        value={propertyData.state || ''}
                        onChange={(e) => handleInputChange('state', e.target.value)}
                        required
                      >
                        <option value="">Select state</option>
                        {nigerianStates.map(state => (
                          <option key={state} value={state}>{state}</option>
                        ))}
                      </select>
                    </div>
                    <div>
                      <Label htmlFor="country">Country</Label>
                      <Input
                        id="country"
                        value={propertyData.country || 'Nigeria'}
                        disabled
                        className="bg-gray-50"
                      />
                    </div>
                  </div>
                </div>

                {/* Price, Period and Size */}
                <div className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="price">Rent Amount (₦) *</Label>
                      <div className="relative">
                        <Input
                          id="price"
                          type="text"
                          placeholder="e.g., 150,000"
                          value={formatCurrency(propertyData.price)}
                          onChange={(e) => handlePriceChange(e.target.value)}
                          required
                          className="pr-12"
                        />
                        <span className="absolute right-3 top-3 text-gray-500 text-sm">₦</span>
                      </div>
                    </div>
                    <div>
                      <Label htmlFor="rentPeriod">Rent Period *</Label>
                      <select
                        id="rentPeriod"
                        className="w-full mt-1 p-2 border border-gray-300 rounded-md"
                        value={propertyData.rentPeriod || 'monthly'}
                        onChange={(e) => handleInputChange('rentPeriod', e.target.value)}
                        required
                      >
                        {rentPeriods.map(period => (
                          <option key={period.value} value={period.value}>
                            {period.label} - {period.description}
                          </option>
                        ))}
                      </select>
                    </div>
                  </div>

                  {/* Deposit Requirements */}
                  <div className="bg-gray-50 p-4 rounded-lg border">
                    <div className="flex items-start space-x-3">
                      <input
                        type="checkbox"
                        id="requiresUpfrontDeposit"
                        checked={propertyData.requiresUpfrontDeposit}
                        onChange={(e) => handleInputChange('requiresUpfrontDeposit', e.target.checked)}
                        className="mt-1 rounded border-gray-300 text-lavender-600 focus:ring-lavender-500"
                      />
                      <div className="flex-1">
                        <Label htmlFor="requiresUpfrontDeposit" className="font-medium">
                          Requires Upfront Deposit Payment
                        </Label>
                        <p className="text-sm text-gray-600 mt-1">
                          Tenant must pay multiple years rent in advance before moving in
                        </p>

                        {propertyData.requiresUpfrontDeposit && (
                          <div className="mt-3">
                            <Label htmlFor="depositYears">Advance Payment Required</Label>
                            <select
                              id="depositYears"
                              className="w-full mt-1 p-2 border border-gray-300 rounded-md"
                              value={propertyData.depositYears || 1}
                              onChange={(e) => handleInputChange('depositYears', parseInt(e.target.value))}
                            >
                              {depositOptions.map(option => (
                                <option key={option.value} value={option.value}>
                                  {option.label} - {option.description}
                                </option>
                              ))}
                            </select>
                            <div className="mt-2 p-3 bg-blue-50 border border-blue-200 rounded-md">
                              <p className="text-sm text-blue-800">
                                <strong>Total Upfront Cost:</strong> ₦{
                                  formatCurrency((parseFloat(propertyData.price || '0') * propertyData.depositYears).toString())
                                } ({propertyData.depositYears} year{propertyData.depositYears > 1 ? 's' : ''} advance payment)
                              </p>
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>

                  <div>
                    <Label htmlFor="size">Property Size (sqm) - Optional</Label>
                    <Input
                      id="size"
                      type="number"
                      placeholder="e.g., 120"
                      value={propertyData.size || ''}
                      onChange={(e) => handleInputChange('size', e.target.value)}
                      min="1"
                    />
                    <p className="text-sm text-gray-500 mt-1">
                      Enter the total area of the property in square meters (optional)
                    </p>
                  </div>
                </div>

                {/* Room Configuration */}
                <div className="space-y-4">
                  <h4 className="font-medium text-gray-900">Room Configuration</h4>
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    <div>
                      <Label htmlFor="bedroomCount">Bedrooms *</Label>
                      <Input
                        id="bedroomCount"
                        type="number"
                        min="0"
                        value={propertyData.bedroomCount || 0}
                        onChange={(e) => handleInputChange('bedroomCount', parseInt(e.target.value) || 0)}
                        required
                      />
                    </div>
                    <div>
                      <Label htmlFor="masterBedroomCount">Master Bedrooms</Label>
                      <Input
                        id="masterBedroomCount"
                        type="number"
                        min="0"
                        value={propertyData.masterBedroomCount || 0}
                        onChange={(e) => handleInputChange('masterBedroomCount', parseInt(e.target.value) || 0)}
                      />
                    </div>
                    <div>
                      <Label htmlFor="toiletCount">Bathrooms *</Label>
                      <Input
                        id="toiletCount"
                        type="number"
                        min="1"
                        value={propertyData.toiletCount || 0}
                        onChange={(e) => handleInputChange('toiletCount', parseInt(e.target.value) || 0)}
                        required
                      />
                    </div>
                    <div>
                      <Label htmlFor="kitchenCount">Kitchens</Label>
                      <Input
                        id="kitchenCount"
                        type="number"
                        min="0"
                        value={propertyData.kitchenCount || 0}
                        onChange={(e) => handleInputChange('kitchenCount', parseInt(e.target.value) || 0)}
                      />
                    </div>
                  </div>
                </div>

                {/* Property Details */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="ceilingType">Ceiling Type</Label>
                    <select
                      id="ceilingType"
                      className="w-full p-2 border border-gray-300 rounded-md"
                      value={propertyData.ceilingType || ''}
                      onChange={(e) => handleInputChange('ceilingType', e.target.value)}
                    >
                      <option value="">Select ceiling type</option>
                      {ceilingTypes.map(type => (
                        <option key={type} value={type}>{type}</option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <Label htmlFor="floorType">Floor Type</Label>
                    <select
                      id="floorType"
                      className="w-full p-2 border border-gray-300 rounded-md"
                      value={propertyData.floorType || ''}
                      onChange={(e) => handleInputChange('floorType', e.target.value)}
                    >
                      <option value="">Select floor type</option>
                      {floorTypes.map(type => (
                        <option key={type} value={type}>{type}</option>
                      ))}
                    </select>
                  </div>
                </div>

                {/* Availability */}
                <div className="space-y-4">
                  <div className="flex items-center space-x-3">
                    <input
                      type="checkbox"
                      id="readyToLive"
                      checked={propertyData.readyToLive}
                      onChange={(e) => handleInputChange('readyToLive', e.target.checked)}
                      className="rounded border-gray-300"
                    />
                    <Label htmlFor="readyToLive">Property is ready to move in</Label>
                  </div>
                  {!propertyData.readyToLive && (
                    <div>
                      <Label htmlFor="readyToLiveDate">Available Date</Label>
                      <Input
                        id="readyToLiveDate"
                        type="date"
                        value={propertyData.readyToLiveDate || ''}
                        onChange={(e) => handleInputChange('readyToLiveDate', e.target.value)}
                      />
                    </div>
                  )}
                </div>

                {/* Description */}
                <div>
                  <Label htmlFor="description">Property Description *</Label>
                  <textarea
                    id="description"
                    className="w-full mt-1 p-3 border border-gray-300 rounded-md h-32"
                    placeholder="Describe your property, highlighting key features and benefits..."
                    value={propertyData.description || ''}
                    onChange={(e) => handleInputChange('description', e.target.value)}
                    required
                  />
                  <p className="text-sm text-gray-500 mt-1">
                    Provide a detailed description to attract potential tenants
                  </p>
                </div>
              </CardContent>
            </Card>
          )}

          {currentStep === 2 && (
            <div className="space-y-6">
              {/* Bathroom Features */}
              <Card>
                <CardHeader>
                  <CardTitle>Bathroom Features</CardTitle>
                  <CardDescription>Select all applicable bathroom features</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                    {bathroomFeatureOptions.map(option => (
                      <label key={option} className="flex items-center space-x-2 cursor-pointer">
                        <input
                          type="checkbox"
                          checked={propertyData.bathroomFeatures.includes(option)}
                          onChange={() => handleCheckboxChange('bathroomFeatures', option)}
                          className="rounded border-gray-300 text-lavender-600 focus:ring-lavender-500"
                        />
                        <span className="text-sm">{option}</span>
                      </label>
                    ))}
                  </div>
                </CardContent>
              </Card>

              {/* Building Amenities */}
              <Card>
                <CardHeader>
                  <CardTitle>Building Amenities</CardTitle>
                  <CardDescription>Select all available building amenities</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                    {buildingAmenityOptions.map(option => (
                      <label key={option} className="flex items-center space-x-2 cursor-pointer">
                        <input
                          type="checkbox"
                          checked={propertyData.buildingAmenities.includes(option)}
                          onChange={() => handleCheckboxChange('buildingAmenities', option)}
                          className="rounded border-gray-300 text-lavender-600 focus:ring-lavender-500"
                        />
                        <span className="text-sm">{option}</span>
                      </label>
                    ))}
                  </div>
                </CardContent>
              </Card>

              {/* Furnishings */}
              <Card>
                <CardHeader>
                  <CardTitle>Furnishings</CardTitle>
                  <CardDescription>Select all included furnishings</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                    {furnishingOptions.map(option => (
                      <label key={option} className="flex items-center space-x-2 cursor-pointer">
                        <input
                          type="checkbox"
                          checked={propertyData.furnishings.includes(option)}
                          onChange={() => handleCheckboxChange('furnishings', option)}
                          className="rounded border-gray-300 text-lavender-600 focus:ring-lavender-500"
                        />
                        <span className="text-sm">{option}</span>
                      </label>
                    ))}
                  </div>
                </CardContent>
              </Card>

              {/* Kitchen Features */}
              <Card>
                <CardHeader>
                  <CardTitle>Kitchen Features</CardTitle>
                  <CardDescription>Select all kitchen features</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                    {kitchenFeatureOptions.map(option => (
                      <label key={option} className="flex items-center space-x-2 cursor-pointer">
                        <input
                          type="checkbox"
                          checked={propertyData.kitchenFeatures.includes(option)}
                          onChange={() => handleCheckboxChange('kitchenFeatures', option)}
                          className="rounded border-gray-300 text-lavender-600 focus:ring-lavender-500"
                        />
                        <span className="text-sm">{option}</span>
                      </label>
                    ))}
                  </div>
                </CardContent>
              </Card>

              {/* Utilities */}
              <Card>
                <CardHeader>
                  <CardTitle>Utilities & Services</CardTitle>
                  <CardDescription>Select all included utilities and services</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                    {utilityOptions.map(option => (
                      <label key={option} className="flex items-center space-x-2 cursor-pointer">
                        <input
                          type="checkbox"
                          checked={propertyData.utilities.includes(option)}
                          onChange={() => handleCheckboxChange('utilities', option)}
                          className="rounded border-gray-300 text-lavender-600 focus:ring-lavender-500"
                        />
                        <span className="text-sm">{option}</span>
                      </label>
                    ))}
                  </div>
                </CardContent>
              </Card>
            </div>
          )}

          {currentStep === 3 && (
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Camera className="h-5 w-5 mr-2" />
                  Property Media
                </CardTitle>
                <CardDescription>
                  Upload high-quality photos and videos of your property
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-6">
                {/* Image Upload */}
                <div>
                  <Label>Property Photos * ({uploadedImages.length}/10)</Label>
                  <div className="mt-2">
                    {/* Upload Area */}
                    <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center hover:border-gray-400 transition-colors">
                      <Upload className="h-8 w-8 text-gray-400 mx-auto mb-4" />
                      <p className="text-gray-600 mb-2">Drag and drop photos here, or click to browse</p>
                      <p className="text-sm text-gray-500 mb-4">Upload up to 10 high-quality photos (JPEG, PNG, max 5MB each)</p>
                      <input
                        type="file"
                        ref={fileInputRef}
                        onChange={handlePhotoUpload}
                        multiple
                        accept="image/*"
                        className="hidden"
                        disabled={isUploadingImages || uploadedImages.length >= 10}
                      />
                      <Button 
                        variant="outline" 
                        onClick={() => fileInputRef.current?.click()}
                        disabled={isUploadingImages || uploadedImages.length >= 10}
                      >
                        {isUploadingImages ? 'Uploading...' : 'Choose Photos'}
                      </Button>
                    </div>

                    {/* Photo Previews */}
                    {imagesPreviews.length > 0 && (
                      <div className="mt-4 grid grid-cols-2 md:grid-cols-4 gap-4">
                        {imagesPreviews.map((preview, index) => (
                          <div key={index} className="relative group">
                            <img
                              src={preview}
                              alt={`Preview ${index + 1}`}
                              className="w-full h-24 object-cover rounded-lg border border-gray-200"
                            />
                            <button
                              type="button"
                              onClick={() => removePhoto(index)}
                              className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full p-1 opacity-0 group-hover:opacity-100 transition-opacity hover:bg-red-600"
                            >
                              <X className="h-3 w-3" />
                            </button>
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                </div>

                {/* Video URL */}
                <div>
                  <Label htmlFor="videoUrl">Property Video URL (Optional)</Label>
                  <Input
                    id="videoUrl"
                    placeholder="https://youtube.com/watch?v=... or https://youtu.be/..."
                    value={propertyData.videoUrl || ''}
                    onChange={(e) => handleInputChange('videoUrl', e.target.value)}
                    className="mt-2"
                  />
                  <p className="text-sm text-gray-500 mt-1">
                    Paste a YouTube, Vimeo, or other video URL to showcase your property
                  </p>
                </div>

                {/* Photo Tips */}
                <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                  <h4 className="font-medium text-blue-900 mb-2">Photography Tips</h4>
                  <ul className="text-sm text-blue-800 space-y-1">
                    <li>• Take photos during daylight hours for best lighting</li>
                    <li>• Include photos of all rooms, kitchen, and bathrooms</li>
                    <li>• Show exterior views and any amenities</li>
                    <li>• Ensure photos are clear and high resolution</li>
                    <li>• First photo will be used as the main display image</li>
                  </ul>
                </div>
              </CardContent>
            </Card>
          )}

          {currentStep === 4 && (
            <Card>
              <CardHeader>
                <CardTitle>Review Your Listing</CardTitle>
                <CardDescription>
                  Please review all information before submitting for approval
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-6">
                {/* Property Summary */}
                <div className="bg-gray-50 p-4 rounded-lg">
                  <h4 className="font-medium text-gray-900 mb-3">Property Summary</h4>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                    <div>
                      <span className="text-gray-600">Type:</span>
                      <span className="ml-2 font-medium">{propertyData.propertyType || 'Not specified'}</span>
                    </div>
                    <div>
                      <span className="text-gray-600">Price:</span>
                      <span className="ml-2 font-medium">
                        ₦{formatCurrency(propertyData.price)} ({propertyData.rentPeriod})
                        {propertyData.requiresUpfrontDeposit && (
                          <span className="block text-xs text-blue-600 mt-1">
                            + ₦{formatCurrency((parseFloat(propertyData.price || '0') * propertyData.depositYears).toString())} upfront
                          </span>
                        )}
                      </span>
                    </div>
                    <div>
                      <span className="text-gray-600">Location:</span>
                      <span className="ml-2 font-medium">{propertyData.city}, {propertyData.state}</span>
                    </div>
                    <div>
                      <span className="text-gray-600">Rooms:</span>
                      <span className="ml-2 font-medium">{propertyData.bedroomCount} bed, {propertyData.toiletCount} bath</span>
                    </div>
                  </div>
                </div>

                {/* Selected Features */}
                <div>
                  <h4 className="font-medium text-gray-900 mb-3">Selected Features</h4>
                  <div className="space-y-4">
                    {propertyData.bathroomFeatures.length > 0 && (
                      <div>
                        <p className="text-sm font-medium text-gray-700">Bathroom Features:</p>
                        <div className="flex flex-wrap gap-2 mt-1">
                          {propertyData.bathroomFeatures.map(feature => (
                            <Badge key={feature} variant="secondary">{feature}</Badge>
                          ))}
                        </div>
                      </div>
                    )}
                    {propertyData.buildingAmenities.length > 0 && (
                      <div>
                        <p className="text-sm font-medium text-gray-700">Building Amenities:</p>
                        <div className="flex flex-wrap gap-2 mt-1">
                          {propertyData.buildingAmenities.map(amenity => (
                            <Badge key={amenity} variant="secondary">{amenity}</Badge>
                          ))}
                        </div>
                      </div>
                    )}
                    {propertyData.furnishings.length > 0 && (
                      <div>
                        <p className="text-sm font-medium text-gray-700">Furnishings:</p>
                        <div className="flex flex-wrap gap-2 mt-1">
                          {propertyData.furnishings.map(furnishing => (
                            <Badge key={furnishing} variant="secondary">{furnishing}</Badge>
                          ))}
                        </div>
                      </div>
                    )}
                    {propertyData.kitchenFeatures.length > 0 && (
                      <div>
                        <p className="text-sm font-medium text-gray-700">Kitchen Features:</p>
                        <div className="flex flex-wrap gap-2 mt-1">
                          {propertyData.kitchenFeatures.map(feature => (
                            <Badge key={feature} variant="secondary">{feature}</Badge>
                          ))}
                        </div>
                      </div>
                    )}
                    {propertyData.utilities.length > 0 && (
                      <div>
                        <p className="text-sm font-medium text-gray-700">Utilities & Services:</p>
                        <div className="flex flex-wrap gap-2 mt-1">
                          {propertyData.utilities.map(utility => (
                            <Badge key={utility} variant="secondary">{utility}</Badge>
                          ))}
                        </div>
                      </div>
                    )}

                    {/* Property Specifications */}
                    {(propertyData.ceilingType || propertyData.floorType) && (
                      <div>
                        <p className="text-sm font-medium text-gray-700">Property Specifications:</p>
                        <div className="flex flex-wrap gap-2 mt-1">
                          {propertyData.ceilingType && (
                            <Badge variant="outline">Ceiling: {propertyData.ceilingType}</Badge>
                          )}
                          {propertyData.floorType && (
                            <Badge variant="outline">Floor: {propertyData.floorType}</Badge>
                          )}
                        </div>
                      </div>
                    )}

                    {/* Media Information */}
                    <div>
                      <p className="text-sm font-medium text-gray-700">Media:</p>
                      <div className="mt-1">
                        <p className="text-sm text-gray-600">
                          📷 {propertyData.imageUrls.length} photo{propertyData.imageUrls.length !== 1 ? 's' : ''} uploaded
                        </p>
                        {propertyData.videoUrl && (
                          <p className="text-sm text-gray-600">
                            🎥 Video URL provided
                          </p>
                        )}
                      </div>
                    </div>

                    {/* Deposit Information */}
                    {propertyData.requiresUpfrontDeposit && (
                      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3">
                        <p className="text-sm font-medium text-yellow-800">⚠️ Upfront Payment Required</p>
                        <p className="text-sm text-yellow-700 mt-1">
                          Tenant must pay {propertyData.depositYears} year{propertyData.depositYears > 1 ? 's' : ''} rent in advance 
                          (₦{formatCurrency((parseFloat(propertyData.price || '0') * propertyData.depositYears).toString())})
                        </p>
                      </div>
                    )}
                  </div>
                </div>

                {/* Admin Review Notice */}
                <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
                  <h4 className="font-medium text-yellow-900 mb-2">Review Process</h4>
                  <p className="text-sm text-yellow-800">
                    Your property will be reviewed by our admin team before going live. This typically takes 24-48 hours. 
                    You'll receive an email notification once your property is approved or if any changes are needed.
                  </p>
                </div>
              </CardContent>
            </Card>
          )}
        </div>

        {/* Navigation Buttons */}
        <div className="flex items-center justify-between mt-8">
          <Button
            variant="outline"
            onClick={() => setCurrentStep(Math.max(1, currentStep - 1))}
            disabled={currentStep === 1}
          >
            Previous
          </Button>

          {currentStep < steps.length ? (
            <Button
              onClick={handleNextStep}
              className="bg-lavender-600 hover:bg-lavender-700"
            >
              Next
            </Button>
          ) : (
            <Button
              onClick={handleSubmit}
              disabled={isSubmitting}
              className="bg-lavender-600 hover:bg-lavender-700"
            >
              {isSubmitting ? (isEditing ? 'Updating...' : 'Submitting...') : (isEditing ? 'Update Property' : 'Submit for Review')}
            </Button>
          )}
        </div>
      </div>
    </div>
  )
}

